INSERT INTO `crewmembers` VALUES(6, 'Tim ', 'Lyda', 'images/roster_headshots/tim _lyda_headshot.jpg', 'Tim started on the crew in 1985, worked a couple of years at Mt. St. Helens, came back and hasn&#039;t left. He has seen them come and seen them go. He has a fancy for pirates and has named his flight helmet the Jolly Roger. He has also been infected with P90X, so if you see him around town, throw up an X and tell him it&#039;s been brung.', '541-761-2676', 'tmlyda@fs.fed.us, tim@siskiyourappellers.com', '415 SE I St.', '', 'Grants Pass', 'Or', '97526', 0);
